ValidationRule.errorConditionFormula:
AND( $Profile.Name <> 'System Administrator', $Permission.EDRD_MOH_STAFF = TRUE, RecordType.DeveloperName = 'EDRD', OR( ISPICKVAL(Status, 'Funding Approved'), ISPICKVAL(Status, 'Funding Denied'), ISPICKVAL(Status, 'Cancelled') ),
NOT( OR( ISCHANGED(Status), ISCHANGED(EDRD_Case_closure_reasons__c), ISCHANGED(EDRD_Notes__c), ISCHANGED(EDRD_MOHFunding_Decision__c), ISCHANGED(EDRD_MOHComments__c), ISCHANGED(EDRD_MOHFunding_Start_Date__c), ISCHANGED(EDRD_MOHFunding_Expiry_Date__c), ISCHANGED(EDRD_MOHIndefinite_Funding__c), ISCHANGED(EDRD_MOHDrug_Cost_Forecast__c), ISCHANGED(EDRD_MOHApproved_Dosage__c), ISCHANGED(EDRD_MOHApproved_Dosage_Unit__c), ISCHANGED(EDRD_MOHApproved_Frequency__c), ISCHANGED(EDRD_MOHApproved_Frequency_Unit__c), ISCHANGED(EDRD_MOHApproved_Funding_Duration__c), ISCHANGED(EDRD_MOHApproved_Funding_Duration_Unit__c) ) )
)
